// TrainList.js
import React, { useEffect, useState } from 'react';
import { getAllTrains } from '../API';
import { Card, CardContent, Typography, Button } from '@mui/material';

function TrainList() {
  const [trains, setTrains] = useState([]);

  useEffect(() => {
    // Fetch all trains when the component mounts
    getAllTrains()
      .then((response) => setTrains(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div>
      <h2>All Trains</h2>
      {trains.map((train) => (
        <Card key={train.trainNumber} style={{ margin: '1rem' }}>
          <CardContent>
            <Typography variant="h6">{train.trainName}</Typography>
            <Typography variant="body1">
              Train Number: {train.trainNumber}
            </Typography>
            <Typography variant="body1">
              Departure Time: {train.departureTime.Hours}:{train.departureTime.Minutes}
            </Typography>
            <Typography variant="body1">
              Sleeper Seats Available: {train.seatsAvailable.sleeper}
            </Typography>
            <Typography variant="body1">
              AC Seats Available: {train.seatsAvailable.AC}
            </Typography>
            <Typography variant="body1">
              Sleeper Price: ${train.price.sleeper}
            </Typography>
            <Typography variant="body1">
              AC Price: ${train.price.AC}
            </Typography>
            <Typography variant="body1">
              Delayed By: {train.delayedBy} minutes
            </Typography>
          </CardContent>
          <Button variant="contained" color="primary">
            View Details
          </Button>
        </Card>
      ))}
    </div>
  );
}

export default TrainList;
